function onChange(control, oldValue, newValue, isLoading) {
    // if (isLoading || newValue == '') {
    //     return;
    // }

	//line 2-4 was commented (removed) so the script can be executed onLoad and onChange

    //Type appropriate comment here, and begin script below

    var user_sysid = g_form.getValue('requested_for');
    // var user_sysid = '0e826bf03710200044e0bfc8bcbe5d7c';


    console.log('user = ' + user_sysid);

    var ga = new GlideAjax('x_1808462_mysdesk.UserDetailsClient');
    ga.addParam('sysparm_name', 'getUserdetails');
    ga.addParam('sysparm_usersysid', user_sysid);
    ga.getXML(getUserdetailsAnswer);

}

function getUserdetailsAnswer(response) {
    var answer = response.responseXML.documentElement.getAttribute('answer');

    // g_form.setValue('comp_location', answer);

    console.log('Raw answer from Script Include: ' + answer);


    try {
        var obj = JSON.parse(answer);
        console.log('Parsed object:', obj);

        // Populate catalog variables
        g_form.setValue('comp_location', obj.location);
        g_form.setValue('email_address', obj.email);
        g_form.setValue('company_name', obj.company);

		var compLoc = obj.company + ", " + obj.location;
		console.log('Home Company Location:', compLoc);

        g_form.setValue('home_company_location', compLoc);


    } catch (e) {
        console.error('Error parsing response: ', e);
    }
}
