catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

	    console.log('New Value:', newValue);

    // Define the field and the message
    var fieldName = 'shipping_location'; // Replace with the actual field name
	var message;

    // Display a message only if the new value is 'yes'
    if (newValue === 'ship_company') {
        message = 'A notification will be send by email once the item(s) are ready for pickup at the company location bellow';
    } 

	else {
        message = 'A notification including tracking information, will be send by email once the item(s) have been shipped to the address provided bellow';
    }

	g_form.showFieldMsg(fieldName, message, 'info');
}
