catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Define the field and the message
    var fieldName = 'is_this_request_for_a_broken_item'; // Replace with the actual field name

    // Display a message only if the new value is 'yes'
    if (newValue === 'Yes') {
        var message = 'Please do not submit this request for broken hardware. If this is a request for broken hardware, per company policy, please report your broken hardware issue to the Help Desk as an Incident (000-111-2222). Your local Desktop Services area will work with you to get your issue resolved. You can also navigate to the Report an Issue Catalog Item to self submit this issu.';
        g_form.showFieldMsg(fieldName, message, 'info');

		return false;
	}

	return true;
}
