catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    var loc = g_form.getValue('comp_location');
    var comp = g_form.getValue('company_name');
    var type = g_form.getValue('request_type');

    // g_form.setMandatory('requested_item', true);
    // g_form.setMandatory('select_operating_system', true);
    // g_form.setMandatory('quantity', true);
    // g_form.setMandatory('business_justification', true);


    var message = "For any return, please bring or ship your item to your home company location mentioned bellow.";

    console.log('Type: ' + type);

    // Correct logical condition
    if (type !== 'return' && type !== '') {
        g_form.setDisplay('requested_item', true);
        g_form.setDisplay('select_operating_system', true);
        g_form.setDisplay('quantity', true);
        g_form.setDisplay('business_justification', true);
        g_form.setDisplay('home_company_location', false);


        // Correct field name
    } else {
        g_form.setDisplay('home_company_location', true);
        g_form.setDisplay('requested_item', false);
        g_form.setDisplay('select_operating_system', false);
        g_form.setDisplay('quantity', false);
        g_form.setDisplay('business_justification', false);
        g_form.showFieldMsg('request_type', message, 'info');

    }
}
