catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

    //var managerField = 'manager';
    var mger = g_form.getValue('manager');
    var reqstor = g_form.getValue('requested_for');
    var message;

    if (newValue === '') {
        // If the triggering field has no value
        message = 'You do not have a manager assigned, please choose one.';
        g_form.setReadOnly('manager', false);
        g_form.setMandatory('manager', true);
        g_form.showFieldMsg('manager', message, 'info');

    } else if (newValue === reqstor) {
        // If the triggering field has a value
        g_form.clearValue('manager');
        message = 'You cannot pick yourself as manager!';
        g_form.showFieldMsg('manager', message, 'error');
        g_form.setReadOnly('manager', false);
        g_form.setMandatory('manager', true);

    } else {
        g_form.setReadOnly('manager', true);
    }

}
