catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
//    if (isLoading || newValue == '') {
//       return;
//    }

    // 1. Populate State dropdown onLoad
    var ga = new GlideAjax('StateCityZipAPI');
    ga.addParam('sysparm_name', 'getStates');
    ga.getXMLAnswer(function(response) {
        var states = JSON.parse(response);
        g_form.clearOptions('state');
        g_form.addOption('state', '', '-- Select State --');
        states.forEach(function(s) {
            g_form.addOption('state', s.name, s.name);
        });
    });

    // 2. Attach onchange listener for State
    g_form.setOnChange('state', function(control, oldValue, newValue, isLoading) {
        if (isLoading || !newValue) {
            g_form.clearOptions('city');
            return;
        }

        var gaCities = new GlideAjax('StateCityZipAPI');
        gaCities.addParam('sysparm_name', 'getCities');
        gaCities.addParam('sysparm_state', newValue);
        gaCities.getXMLAnswer(function(response) {
            var cities = JSON.parse(response);
            g_form.clearOptions('city');
            g_form.addOption('city', '', '-- Select City --');
            cities.forEach(function(c) {
                g_form.addOption('city', c.name, c.name);
            });
        });
    });

    // 3. Attach onchange listener for City
    g_form.setOnChange('city', function(control, oldValue, newValue, isLoading) {
        if (isLoading || !newValue || !g_form.getValue('state')) return;

        var city = newValue;
        var state = g_form.getValue('state');

        var gaZip = new GlideAjax('StateCityZipAPI');
        gaZip.addParam('sysparm_name', 'getZip');
        gaZip.addParam('sysparm_city', city);
        gaZip.addParam('sysparm_state_name', state);
        gaZip.getXMLAnswer(function(zip) {
            g_form.setValue('zip_code', zip || '');
        });
    });
}
