catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    // if (isLoading || !newValue) {
    //     return;
    // }

	var zip = g_form.getValue('zip_code');

    console.log('ZIP Entered: ' + newValue);

    var ga = new GlideAjax('ZipCodeLookup');
    ga.addParam('sysparm_name', 'getCityStateFromZip');
    ga.addParam('sysparm_zipcode', zip);


    if (zip === '' || zip.length != 5) {

        g_form.setDisplay('street_address', false);
        g_form.setDisplay('appartment_num', false);
        g_form.setDisplay('city', false);
        g_form.setDisplay('state', false);

        var message = 'Enter a five digit zip code';

        g_form.showFieldMsg('zip_code', message, 'info');

    } else {
        g_form.setDisplay('street_address', true);
        g_form.setDisplay('appartment_num', true);
        g_form.setDisplay('city', true);
        g_form.setReadOnly('city', true);
        g_form.setDisplay('state', true);
        g_form.setReadOnly('state', true);


        ga.getXMLAnswer(function(response) {
            console.log('Response from Script Include: ' + response);

            try {
                var result = JSON.parse(response);

                if (result.status === 'success') {
                    g_form.setValue('city', result.city);
                    g_form.setValue('state', result.state);
                    console.log('City set to: ' + result.city + ', State set to: ' + result.state);
                } else {
                    g_form.clearValue('city');
                    g_form.clearValue('state');
                    alert('Could not fetch City/State: ' + (result.error || 'Unknown error'));
                }

            } catch (err) {
                console.error('Failed to parse response: ' + err);
                g_form.clearValue('city');
                g_form.clearValue('state');
            }
        });
    }
}
