sys_variable_value.value:
(function execute(inputs, outputs) {
    var responseObject = {};

    if (inputs.response_body) {
        try {
            responseObject = JSON.parse(inputs.response_body);
        } catch (e) {
            gs.error("Failed to parse response_body: " + e.message);
        }
    }

    // Match exactly the Output variable names you defined in Outputs tab
    outputs.order_number = responseObject.order_number || "";
    // outputs.hardware_serials = responseObject.hardware_serials || [];
    outputs.confirmation_number = responseObject.confirmationNumber || "";
    outputs.hardware_serials = responseObject.hardware_serials.join("\n");
    outputs.quantity = inputs.quantity || "";
})(inputs, outputs);

