sys_script_include.script:
var UserDetailsServer = Class.create();
UserDetailsServer.prototype = {
    initialize: function() {},

    userInfo: function(usersysid) {

		var result = {};

        var list = new GlideRecord("sys_user");
        // list.addEncodedQuery("");
        list.addQuery('sys_id', usersysid);
        list.query();
        if (list.next()) {
            // gs.info(list.getDisplayValue());
            result.location = list.getDisplayValue('location');
            result.email = list.getDisplayValue('email');
            result.company = list.getDisplayValue('company');
        }

		return JSON.stringify(result);

    },

    type: 'UserDetailsServer'
};
