sys_script_include.script:
var ZipCodeLookup = Class.create();
ZipCodeLookup.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getCityStateFromZip: function() {
        var zipCode = this.getParameter('sysparm_zipcode');

        if (!zipCode) {
            return JSON.stringify({ error: 'No ZIP code provided' });
        }

        try {
            var r = new sn_ws.RESTMessageV2('x_1808462_mysdesk.ZipCode API', 'Default GET');
            r.setStringParameterNoEscape('zipCode', zipCode);
            r.setRequestHeader('Content-Type', 'application/json');
            r.setHttpMethod('GET');

            var response = r.execute();
            var httpStatus = response.getStatusCode();
            var responseBody = response.getBody();

            if (httpStatus == 200 && responseBody) {
                var data = JSON.parse(responseBody);
                return JSON.stringify({
                    city: data.city || '',
                    state: data.state || '',
                    status: 'success'
                });
            } else {
                return JSON.stringify({ error: 'Failed with status: ' + httpStatus });
            }

        } catch (ex) {
            return JSON.stringify({ error: 'Exception: ' + ex.message });
        }
    },

    type: 'ZipCodeLookup'
});
