sys_script_include.script:
var StateCityZipAPI = Class.create();
StateCityZipAPI.prototype = Object.extendsObject(AbstractAjaxProcessor, {
  getStates: function() {
    var r = new sn_ws.RESTMessageV2();
    r.setEndpoint('https://countrystatecity.in/api/states/1');
    r.setHttpMethod('GET');
    var response = r.execute();
    return response.getBody(); // JSON list of states
  },

  getCities: function() {
    var stateId = this.getParameter('sysparm_state');
    var r = new sn_ws.RESTMessageV2();
    r.setEndpoint('https://countrystatecity.in/api/cities/' + stateId);
    r.setHttpMethod('GET');
    var response = r.execute();
    return response.getBody(); // JSON list of cities
  },

  getZip: function() {
    var city = this.getParameter('sysparm_city');
    var state = this.getParameter('sysparm_state_name'); // e.g., "California"
    var url = 'https://api.zippopotam.us/us/' + state + '/' + encodeURIComponent(city);
    var r = new sn_ws.RESTMessageV2();
    r.setEndpoint(url);
    r.setHttpMethod('GET');
    var resp = r.execute();
    if (resp.getStatusCode() == 200) {
      var obj = JSON.parse(resp.getBody());
      return obj.places && obj.places[0] ? obj.places[0]['post code'] : '';
    }
    return '';
  },

  type: 'StateCityZipAPI'
});
